<!-- Include Font Awesome for icons -->
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css">

<style>
    /* Full dark background for the entire page */
    body {
        background-color: #121212;
        color: #ffffff;
        overflow: hidden;
    }

    /* Container padding */
    .main-container {
        padding-top: 30px;
    }

    /* Card styling with rounded edges, shadow, and zoom on hover */
    .card {
        background-color: #1e1e1e;
        color: #ffffff;
        border-radius: 15px;
        box-shadow: 0px 4px 15px rgba(0, 0, 0, 0.5);
        transition: transform 0.3s, box-shadow 0.3s;
    }

    /* Outer card hover effect */
    .card:hover {
        transform: scale(1.05);
        box-shadow: 0px 6px 20px rgba(255, 255, 255, 0.15);
    }

    /* Inner cards for Details and Education */
    .inner-card {
        background-color: #282828;
        border-radius: 10px;
        box-shadow: 0px 2px 10px rgba(0, 0, 0, 0.3);
        margin-bottom: 20px;
    }

    /* Title gradient effect */
    .card-title {
        color: #ffffff;
        background: linear-gradient(90deg, #ff7e5f, #feb47b);
        -webkit-background-clip: text;
        background-clip: text;
        color: transparent;
    }

    /* Divider styling */
    hr {
        background-color: #444;
    }

    /* Enable scrolling for smaller screen sizes */
@media (max-width: 992px) { /* Adjust breakpoint as needed */
    html, body {
        overflow: auto; /* Allow scrolling on smaller screens */
    }
}
</style>

